const countCardsPerBucket = 100;

export async function POST(req) {
  const body = await req.json();
  const seenCardIds = body.seenCardIds || [];
  const currentBucket = body.currentBucket;

  // Filter out cards that have already been seen
  const remainingCards = cards
  .filter(item => item.id >= ((currentBucket - 1) * 100 + 1) && item.id <= currentBucket * 100)
  .filter((card) => !seenCardIds.includes(card.id));

  // Calculate progress (percentage of cards seen)
  const progress = {
    totalSeenCards: seenCardIds.length + 1,
    totalCards: countCardsPerBucket,
    bucketFrom: ((currentBucket - 1) * 100 + 1),
    bucketTo: (currentBucket * 100)
  }

  if (remainingCards.length === 0) {
    return new Response(
      JSON.stringify({ message: 'All cards read!', progress }),
      {
        status: 200,
        headers: { 'Content-Type': 'application/json' },
      }
    );
  }

  // Select a random unseen card
  const randomCard = remainingCards[Math.floor(Math.random() * remainingCards.length)];

  return new Response(
    JSON.stringify({ card: randomCard, progress }),
    {
      status: 200,
      headers: { 'Content-Type': 'application/json' },
    }
  );
}

export async function GET(req) {
  const { searchParams } = new URL(req.url);
  const cardId = searchParams.get('id');

  // Find the card with the provided ID
  const cardResponse = cards.find((card) => card.id === parseInt(cardId));

  if (cardResponse) {
    // Return the card's description (question/answer or both)
    return new Response(
      JSON.stringify({ card: cardResponse}),
      {
        status: 200,
        headers: { 'Content-Type': 'application/json' },
      }
    );
  } else {
    return new Response(
      JSON.stringify({ message: 'Card not found' }),
      {
        status: 404,
        headers: { 'Content-Type': 'application/json' },
      }
    );
  }
}